exports.showItem = function (data, logger) {
  var logger = (logger) ? logger : log('utils.showItem');

  logger.warn(
    '!!!!!! showItem data info: DATA.itemName: ' + data.itemName
    + ', LABEL: ' + items.getItem(data.itemName).label +
    ', STATE: ' + items.getItem(data.itemName).state +
    ', PREV STATExx: ' + items.getItem(data.itemName).history.previousState()
  );
}
